import{_ as s,c as n,o,a8 as a}from"./chunks/framework.6k5lawSO.js";const u=JSON.parse('{"title":"","description":"mongoDB 客户端","frontmatter":{"description":"mongoDB 客户端","tags":["数据库","mongoDB"]},"headers":[],"relativePath":"E.数据库/A.mongoDB/3.mongoDB客户端.md","filePath":"E.数据库/A.mongoDB/3.mongoDB客户端.md","lastUpdated":1686039772000}'),e={name:"E.数据库/A.mongoDB/3.mongoDB客户端.md"},l=a(`<p>mongo Shell 是 MongoDB 官方提供的一个在命令行中用来连接操作 MongoDB 服务的客户端工具，使用 mongo Shell 可以对 MongoDB 数据库进行数据的管</p><p>您可以在没有任何命令行选项的情况下运行 mongo shell，以使用默认端口 27017 连接到在本地主机上运行的 MongoDB 实例</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki one-light vp-code" tabindex="0"><code><span class="line"><span style="color:#4078F2;">mongo</span><span style="color:#50A14F;"> /</span><span style="color:#50A14F;"> mongosh</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="连接非默认端口上的本地-mongodb-服务" tabindex="-1">连接非默认端口上的本地 MongoDB 服务 <a class="header-anchor" href="#连接非默认端口上的本地-mongodb-服务" aria-label="Permalink to &quot;连接非默认端口上的本地 MongoDB 服务&quot;">​</a></h2><p>要明确指定端口，请包括 --port 命令行选项。例如，要使用非默认端口 28015 连接到在 localhost 上运行的 MongoDB 实例，请执行以下操作</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki one-light vp-code" tabindex="0"><code><span class="line"><span style="color:#4078F2;">mongo</span><span style="color:#986801;"> --port</span><span style="color:#986801;"> 28015</span></span>
<span class="line"><span style="color:#4078F2;">mongosh</span><span style="color:#986801;">  --port</span><span style="color:#986801;"> 28015</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h2 id="连接远程主机上的-mongodb-服务" tabindex="-1">连接远程主机上的 MongoDB 服务 <a class="header-anchor" href="#连接远程主机上的-mongodb-服务" aria-label="Permalink to &quot;连接远程主机上的 MongoDB 服务&quot;">​</a></h2><p>连接远程主机上的 MongoDB 服务需要明确指定主机名和端口号。 您可以指定一个连接字符串。例如，要连接到在远程主机上运行的 MongoDB 实例，请执行以下操作</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki one-light vp-code" tabindex="0"><code><span class="line"><span style="color:#4078F2;">mongo</span><span style="color:#50A14F;"> &quot;mongodb://mongodb0.example.com:28015&quot;</span></span>
<span class="line"><span style="color:#4078F2;">mongosh</span><span style="color:#50A14F;"> &quot;mongodb://mongodb0.example.com:28015&quot;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p>您可以使用命令行选项 --host <code>&lt;主机&gt;:&lt;端口&gt;</code>。例如，要连接到在远程主机上运行的 MongoDB 实例，请执行以下操作：</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki one-light vp-code" tabindex="0"><code><span class="line"><span style="color:#4078F2;">mongo</span><span style="color:#986801;"> --host</span><span style="color:#50A14F;"> mongodb0.example.com:28015</span></span>
<span class="line"><span style="color:#4078F2;">mongosh</span><span style="color:#986801;"> --host</span><span style="color:#50A14F;"> mongodb0.example.com:28015</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p>您可以使用--host <code>&lt;host&gt;和--port &lt;port&gt;</code>命令行选项。例如，要连接到在远程主机上运行的MongoDB实例，请执行以下操作</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki one-light vp-code" tabindex="0"><code><span class="line"><span style="color:#4078F2;">mongo</span><span style="color:#986801;"> --host</span><span style="color:#50A14F;"> mongodb0.example.com</span><span style="color:#986801;"> --port</span><span style="color:#986801;"> 28015</span></span>
<span class="line"><span style="color:#4078F2;">mongosh</span><span style="color:#986801;"> --host</span><span style="color:#50A14F;"> mongodb0.example.com</span><span style="color:#986801;"> --port</span><span style="color:#986801;"> 28015</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h2 id="连接具有身份认证的-mongodb-服务" tabindex="-1">连接具有身份认证的 MongoDB 服务 <a class="header-anchor" href="#连接具有身份认证的-mongodb-服务" aria-label="Permalink to &quot;连接具有身份认证的 MongoDB 服务&quot;">​</a></h2><p>您可以在连接字符串中指定用户名，身份验证数据库以及可选的密码。例如，以alice用户身份连接并认证到远程MongoDB实例</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki one-light vp-code" tabindex="0"><code><span class="line"><span style="color:#4078F2;">mongo</span><span style="color:#50A14F;"> &quot;mongodb://alice@mongodb0.examples.com:28015/?authSource=admin&quot;</span></span>
<span class="line"><span style="color:#4078F2;">mongosh</span><span style="color:#50A14F;"> &quot;mongodb://alice@mongodb0.examples.com:28015/?authSource=admin&quot;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p>您可以使用<code>--username &lt;user&gt;和--password，--authenticationDatabase &lt;db&gt;</code>命令行选项。例如，以alice用户身份连接并认证到远程MongoDB实例：</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki one-light vp-code" tabindex="0"><code><span class="line"><span style="color:#4078F2;">mongo</span><span style="color:#986801;"> --username</span><span style="color:#50A14F;"> alice</span><span style="color:#986801;"> --password</span><span style="color:#986801;"> --authenticationDatabase</span><span style="color:#50A14F;"> admin</span><span style="color:#986801;"> --host</span><span style="color:#50A14F;"> mongodb0.examples.com</span><span style="color:#986801;"> --port</span><span style="color:#986801;"> 28015</span></span>
<span class="line"><span style="color:#4078F2;">mongosh</span><span style="color:#986801;"> --username</span><span style="color:#50A14F;"> alice</span><span style="color:#986801;"> --password</span><span style="color:#986801;"> --authenticationDatabase</span><span style="color:#50A14F;"> admin</span><span style="color:#986801;"> --host</span><span style="color:#50A14F;"> mongodb0.examples.com</span><span style="color:#986801;"> --port</span><span style="color:#986801;"> 28015</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div>`,18),p=[l];function t(c,r,i,d,m,g){return o(),n("div",null,p)}const h=s(e,[["render",t]]);export{u as __pageData,h as default};
